    private void botaoConsultarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_botaoConsultarMouseClicked
        modelo.setNumRows(0);
        for(Membro m : dao.buscar(campoNome.getText())){
            modelo.addRow(new Object[]{m.getId(),m.getNome(),m.getSexo(),m.getCpf(),m.getDataNascimento()});   
        }
        campoNome.setText("");
    }//GEN-LAST:event_botaoConsultarMouseClicked

